export const HeartAngle = ({
  size = 24,
  color = "currentColor",
  weight = "outline",
  ...props
}) => {
  const activeWeight = svgs[weight] ? weight : Object.keys(svgs)[0];

  return (
    <svg
      viewBox="0 0 24 24"
      width={size}
      height={size}
      color={color}
      fill="none"
      {...props}
    >
      <g dangerouslySetInnerHTML={{ __html: svgs[activeWeight] }} />
    </svg>
  );
};
